import './App.css';
import catimg from "./cat.png";
import { useState } from 'react';

function App() {
  return (
    <div className="App">
      <header className="App-header">
        {/* <img src={logo} className="App-logo" alt="logo" /> */}


        <h1>Welcome to Katsu!</h1>
      </header>

      <div className = "navbar">
        <div class="navbar_element">
          <a href="#"><img src={catimg} alt="Browse"></img></a>
        </div>

        <div class="navbar_element">
          <a href="#"><img src={catimg} alt="Discover"></img></a>
        </div>

        <div className="navbar_element">
          <a href="#"><img src={catimg} alt="Resources" ></img></a>
        </div>

        <div className="navbar_element">
          <a href="#"><img src={catimg} alt="About"></img></a>
        </div>
      </div>

      <div className="content">
        <p> Welcome to katsu! your very own one-stop-search site for cute cats!</p>
        <h1>Look around to see for cool things or search for something!</h1>

        <RenderSearch></RenderSearch>

        <h1>Results</h1>

        <div className="results"></div>
      </div>


    </div>
  );
}
// create text box input and search button
function RenderSearch() {
  const [query, setQuery] = useState("");
  const [urls, setUrls] = useState("");

  function handleQueryChange(e) {
    setQuery(e.target.value);
  }

  function handleApi() {
    let res = callApi(query)
    setUrls(res);
  }

  return (
    <>
      <h1>Search</h1>
      {/* TEXTBOX: onChange, update query. onEnter, handleApi() */}
      <input onChange={handleQueryChange} 
        onKeyDown={(e) => {
          if (e.key === "Enter")
              handleApi();
          }}  
        type="text" placeholder="small cat drinking pineapple juice..">

        </input>
        {/*SEARCH BTN: onClick, handleApi() */}
      <button onClick={handleApi}>Search!</button>
      <p>{query}</p>
      <p>{urls}</p>
    </>
  );
}

// queries image server
function callApi(query) {
   let api_root = "https://httpbin.org/get" 
  //  let api_link = api_root + query
  let api_link = api_root

  let json_result = ""
   // send GET request 
   fetch(api_link)
    .then(response => response.json())
    .then(data => {
      // Process the API response here
      json_result = data;
    })
    .catch(error => {
      console.error("Error fetching data:", error);
    });

   return (
    <>
    <p>{api_link}</p>
    <p>{json_result}</p>
    </>
   )
}


// INPUT list of IMG urls
// OUTPUT a group of <a> wrapped in div
